    "previous")
        play_previous
        ;;
    "play-pause")
        toggle_play_pause
        ;;
    "stop")
        stop_playback
        ;;
    *)
        echo "Usage: $0 [next|previous|play-pause|stop]"
        exit 1
        ;;
esac#!/usr/bin/env bash

