# Combine into menu_options array
declare -A menu_options
for key in "${!no_shuffle[@]}"; do menu_options["$key"]="${no_shuffle[$key]}"; done
for key in "${!shuffle[@]}"; do menu_options["$key"]="${shuffle[$key]}"; done

# Function for displaying notifications
notification() {
  # Default icon
  local icon="$iDIR/music.png"

  # Option 1: Try to get album art (if ytdl-hook plugin is enabled in mpv)
  # if [ -S "$MPV_SOCKET" ] && [[ "$2" == *"playlist"* ]]; then
  #   thumbnail_url=$(echo '{"command": ["get_property", "metadata/by-key/thumbnail"]}' | socat - "$MPV_SOCKET" 2>/dev/null | jq -r '.data // empty')
  #   if [ -n "$thumbnail_url" ] && [ "$thumbnail_url" != "null" ]; then
  #     # Download thumbnail to temp file and use it
  #     temp_thumb="${XDG_RUNTIME_DIR:-/tmp}/mpv_thumb.jpg"
  #     curl -s "$thumbnail_url" -o "$temp_thumb"
  #     icon="$temp_thumb"
  #   fi
  # fi

  notify-send -e -t 2500 -u normal -i "$iDIR/music.png" "Playing now: $1"
}

# Main function
main() {
  # TODO: increase this value if adding more playlists
  r_override="entry{placeholder:'Search Music...';}listview{lines:10;}"
  choice=$(printf "%s\n" "${!menu_options[@]}" | rofi -dmenu -theme-str "$r_override" -theme ~/.config/rofi/launchers/type-2/style-2.rasi -i -p "")

  if [ -z "$choice" ]; then
    exit 1
  fi

  link="${menu_options[$choice]}"

  notification "$choice"

  # Check if the link is a playlist and handle shuffling
  if [[ $link == *playlist* ]]; then
    if [[ -v no_shuffle["$choice"] ]]; then
      mpv --vid=no "$link"
    else
      mpv --vid=no --shuffle "$link"
    fi
  else
    # Non-playlist links (e.g., radio streams) play without shuffle
    mpv "$link"
  fi
}

# Check if an online music process is running and send a notification, otherwise run the main function
pkill mpv && notify-send -e -t 2500 -u low -i "$iDIR/music.png" "Playback stopped" || main#!/usr/bin/env bash

swpy_dir="${XDG_CONFIG_HOME:-$HOME/.config}/swappy"
XDG_PICTURES_DIR="${XDG_PICTURES_DIR:-$HOME/Pictures}"
save_dir="${XDG_PICTURES_DIR}/Screenshots"
save_file="$(date +'%y%m%d_%Hh%Mm%Ss_screenshot.png')"

mkdir -p $save_dir
mkdir -p $swpy_dir
echo -e "[Default]\nsave_dir=$save_dir\nsave_filename_format=$save_file" > $swpy_dir/config

# Ensure required binaries
command -v grimblast >/dev/null 2>&1 || { echo "grimblast not found"; exit 1; }
command -v swappy >/dev/null 2>&1 || { echo "swappy not found"; exit 1; }
command -v notify-send >/dev/null 2>&1 || { echo "notify-send not found"; exit 1; }

print_error() {
    cat << EOF
Usage: $(basename "$0") <action>
Valid actions:
  p  : Print all screens
  s  : Snip current screen
  sf : Snip current screen (frozen)
  m  : Print focused monitor
EOF
    exit 1
}

case "$1" in
    p)  grimblast save screen - | swappy -f - ;;
    s)  grimblast save area - | swappy -f - ;;
    sf) grimblast --freeze save area - | swappy -f - ;;
    m)  grimblast save output - | swappy -f - ;;
    *)  print_error ;;
esac

# Notify if saved
if [ -f "${save_dir}/${save_file}" ]; then
    notify-send -a Screenshot -r 91190 -t 2200 -i "${save_dir}/${save_file}" "Saved in ${save_dir}"
fi#!/usr/bin/env bash

MODE_FILE="$HOME/.config/hypr/power_mode"

if [[ ! -s "$MODE_FILE" ]]; then
    echo "performance" > "$MODE_FILE"
fi

CURRENT=$(cat "$MODE_FILE")

if [[ "$CURRENT" == "performance" ]]; then
    NEW_MODE="powersave"
    CMD="pkexec tlp bat"
else
    NEW_MODE="performance"
    CMD="pkexec tlp ac"
fi

if $CMD > /dev/null 2>&1; then
    echo "$NEW_MODE" > "$MODE_FILE"
fi

exit 0#!/usr/bin/env sh

function print_error
{
cat << "EOF"
    ./volumecontrol.sh -[device] <action>
    ...valid device are...
        i -- [i]nput decive
        o -- [o]utput device
    ...valid actions are...
        i -- <i>ncrease volume [+5]
        d -- <d>ecrease volume [-5]
        m -- <m>ute [x]
EOF
}

function notify_vol
{
    vol=`pamixer $srce --get-volume | cat`
    angle=$((((vol + 2) / 5) * 5))
    ico="${icodir}/vol-${angle}.svg"
    bar=$(seq -s "." $(($vol / 15)) | sed 's/[0-9]//g')
    notify-send -a "System" -r 91190 -t 800 -i "${ico}" "${vol}${bar}" "$nsink"
}

function notify_mute
{
    mute=`pamixer $srce --get-mute | cat`
    if [ "$mute" == "true" ] ; then
        notify-send -a "System" -r 61190 -t 800 -i "${icodir}/muted-${dvce}.svg" "Muted" "$nsink"
    else
        notify-send -a "System" -r 61190 -t 800 -i "${icodir}/unmuted-${dvce}.svg" "Unmuted" "$nsink"
    fi
}


# set device source

while getopts io SetSrc
do
    case $SetSrc in
    i) nsink=$(pamixer --list-sources | grep "_input." | head -1 | awk -F '" "' '{print $NF}' | sed 's/"//')
        srce="--default-source"
        dvce="mic" ;;
    o) nsink=$(pamixer --get-default-sink | grep "_output." | awk -F '" "' '{print $NF}' | sed 's/"//')
        srce=""
        dvce="speaker" ;;
    esac
done

if [ $OPTIND -eq 1 ] ; then
    print_error
fi

# set device action
shift $((OPTIND -1))
step="${2:-1}"
icodir="$HOME/.config/hypr/icons/notifications/vol"

case $1 in
    i) pamixer $srce -i ${step}
        notify_vol ;;
    d) pamixer $srce -d ${step}
        notify_vol ;;
    m) pamixer $srce -t
        notify_mute ;;
    *) print_error ;;
esac#!/usr/bin/env bash
## /* ---- 💫 https://github.com/JaKooLit 💫 ---- */  ##

# Not my own work. This was added through Github PR. Credit to original author

#----- Optimized bars animation without much CPU usage increase --------
bar="▁▂▃▄▅▆▇█"
dict="s/;//g"

# Calculate the length of the bar outside the loop
bar_length=${#bar}

# Create dictionary to replace char with bar
for ((i = 0; i < bar_length; i++)); do
    dict+=";s/$i/${bar:$i:1}/g"
done

# Create cava config
config_file="/tmp/bar_cava_config"
cat >"$config_file" <<EOF
[general]
bars = 10
;framerate = 60
;sensitivity = 120

[input]
method = pulse
source = auto

[output]
method = raw
raw_target = /dev/stdout
data_format = ascii
ascii_max_range = 7
EOF
# ascii_max_range = 9

# Kill cava if it's already running
pkill -f "cava -p $config_file"

# create a cava process for each monitor
for monitor in $(hyprctl monitors | grep "ID" | awk '{print $2}'); do
		cava -p "$config_file" | sed -u "$dict"
done#!/usr/bin/env bash

