xrandr \
  --output "$MON_LEFT"  --mode 1920x1080 --scale 1x1     --pos 0x0 --primary \
  --output "$MON_MID"   --mode 3840x2160 --scale 0.5x0.5 --pos 1920x0 \
  --output "$MON_RIGHT" --mode 1920x1080 --rotate left   --scale 1x1 --pos 3840x-420

